                                                                                                                                                                                                                                                   
#include "DiemThi.h"
#include "Scanner.h"



void DiemThi::OutputFStream(ofstream & f)
{
	f << maMonHoc << " | ";
	f << diem;
}

void DiemThi::InputFStream(ifstream & f)
{
	Scanner scan;
	string value;
	scan.LayDuLieu(value, f);
	if (value != "")
		maMonHoc = value;

	value = "";
	scan.LayDuLieu(value, f);
	if (value != "")
		strcpy_s(diem,_strdup(value.c_str()));
}
